CREATE TABLE "public"."raid_contributions" ("id" bigserial NOT NULL, "raid_id" integer NOT NULL, "player_id" text NOT NULL, "contribution" integer NOT NULL, "faction" text, "contribution_timestamp" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("player_id") REFERENCES "public"."players"("id") ON UPDATE no action ON DELETE no action, FOREIGN KEY ("raid_id") REFERENCES "public"."raids"("id") ON UPDATE no action ON DELETE no action, FOREIGN KEY ("faction") REFERENCES "public"."rat_types"("rat_type") ON UPDATE no action ON DELETE no action);
