CREATE TABLE "public"."gauntlet_attempts" ("id" bigserial NOT NULL, "gauntlet_id" integer NOT NULL, "player_id" text NOT NULL, "result" boolean, "last_attempted_encounter_id" integer NOT NULL, "started_timestamp" timestamptz NOT NULL DEFAULT now(), "completed_timestamp" timestamptz, PRIMARY KEY ("id") , FOREIGN KEY ("gauntlet_id") REFERENCES "public"."gauntlets"("id") ON UPDATE no action ON DELETE no action, FOREIGN KEY ("player_id") REFERENCES "public"."players"("id") ON UPDATE no action ON DELETE no action, FOREIGN KEY ("last_attempted_encounter_id") REFERENCES "public"."encounters"("id") ON UPDATE no action ON DELETE no action);
